con:configuration.path:
declare namespace soap='http://schemas.xmlsoap.org/soap/envelope/';
declare namespace ns1='http://www.27seconds.com/Holidays/';

for $x in //ns1:GetHolidayDateResult
return $x/text()

declare namespace ns1='';
declare namespace soap='http://schemas.xmlsoap.org/soap/envelope/';
declare namespace diffgr='urn:schemas-microsoft-com:xml-diffgram-v1';
declare namespace ns2='http://www.27seconds.com/Holidays/';
declare namespace msdata='urn:schemas-microsoft-com:xml-msdata';
declare namespace xs='http://www.w3.org/2001/XMLSchema';

<Result>
{
for $x in //diffgr:diffgram/NewDataSet/Countries
order by $x/Code
return <Name>{data($x/Name/text())}</Name>
}
</Result>

con:configuration.scriptText:
import com.eviware.soapui.support.XmlHolder

// check that response time is less than 1200 ms
assert messageExchange.timeTaken < 12000



// check for RequestId element in response
def holder = new XmlHolder( messageExchange.responseContentAsXml )
assert holder["//ns1:RequestId"] != null



//check number of holidays nodes
log.info("Number of Hollidays:" + holder.getNodeValues( "//Name" ).size())
assert holder.getNodeValues( "//Name" ).size() == 34




// loop item nodes in response message
log.info "================================================"
for( item in holder.getNodeValues( "//Name" ))
    log.info "Item : [$item]"

con:transfers.con:sourcePath:
declare namespace ns1='';
declare namespace soap='http://schemas.xmlsoap.org/soap/envelope/';
declare namespace diffgr='urn:schemas-microsoft-com:xml-diffgram-v1';
declare namespace ns2='http://www.27seconds.com/Holidays/';
declare namespace msdata='urn:schemas-microsoft-com:xml-msdata';
declare namespace xs='http://www.w3.org/2001/XMLSchema';

<Result>
{
for $x in //diffgr:diffgram/NewDataSet/Countries
where $x/Name = 'UK - Northern Ireland'
return data($x/Code/text())
}
</Result>

con:config.script:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )

//
// Get Holiday key from response
//

def response = context.expand('${GetHolidaysAvailable#Response}')

XmlParser parser = new XmlParser()
def HolidaysAvailable = parser.parseText (response)

assert HolidaysAvailable instanceof groovy.util.Node 


def HolidayKey = HolidaysAvailable.'**'.find { Holidays->
    Holidays.Name.text() == 'April Fools Day'
}.Key.text()

//
// Write Holiday Key to request
//

def request = context.expand('${GetHolidayDate#Request')

def holder = groovyUtils.getXmlHolder( "GetHolidayDate#Request" )

holder["//hol:holidayName"] = HolidayKey
holder.updateProperty()

con:condition.con:expression:
declare namespace soap='http://schemas.xmlsoap.org/soap/envelope/';
declare namespace ns1='http://www.27seconds.com/Holidays/';

exists(//ns1:GetHolidayDateResult)
